// Mock Chart.js implementation to prevent errors
window.Chart = {
    register: function() {},
    defaults: {},
    Chart: class {
        constructor(ctx, config) {}
        update() {}
        destroy() {}
    }
};
// 创建一个基本的图表模拟函数
function createBasicChart(canvasId, labels, data, bgColors) {
    const canvas = document.getElementById(canvasId);
    if (!canvas) return;
    
    const ctx = canvas.getContext('2d');
    const width = canvas.width;
    const height = canvas.height;
    
    // 清空画布
    ctx.clearRect(0, 0, width, height);
    
    // 绘制简单的柱状图
    const barWidth = width / labels.length * 0.6;
    const gap = width / labels.length * 0.4 / 2;
    const maxValue = Math.max(...data);
    
    labels.forEach((label, index) => {
        const barHeight = (data[index] / maxValue) * (height - 60);
        const x = gap + index * (barWidth + gap * 2);
        const y = height - 50 - barHeight;
        
        // 绘制柱子
        ctx.fillStyle = bgColors[index % bgColors.length];
        ctx.fillRect(x, y, barWidth, barHeight);
        
        // 绘制标签
        ctx.fillStyle = '#333';
        ctx.font = '12px Arial';
        ctx.textAlign = 'center';
        ctx.fillText(label, x + barWidth / 2, height - 30);
        
        // 绘制数值
        ctx.fillText(data[index], x + barWidth / 2, y - 10);
    });
    
    // 绘制标题
    ctx.font = '16px Arial';
    ctx.fillText('健康数据趋势', width / 2, 30);
}